:root {
  /* Brand Colors (Nosta) */
  --chat--color-primary: #009EE3; /* Nosta Azure‑blue */
  --chat--color-primary-shade-50: #007bb8;
  --chat--color-primary-shade-100: #005d8f;
  --chat--color-secondary: #582DC6; /* Nosta Violet accent */
  --chat--color-secondary-shade-50: #461fa1;
  --chat--color-secondary-shade-100: #36197a;
  --chat--color-white: #FFFFFF;
  --chat--color-light: #E4E4E4; /* light grey surfaces & borders */
  --chat--color-light-shade-50: #d1d1d1;
  --chat--color-light-shade-100: #bdbdbd;
  --chat--color-medium: #B5BCC9;
  --chat--color-dark: #000000; /* solid black text */
  --chat--color-disabled: #9FA6B2;
  --chat--color-typing: #555555;
  --chat--header-height: 4rem;

  /* Layout & Fonts */
  --chat--spacing: 1rem;
  --chat--border-radius: 0.5rem;
  --chat--font-family: 'Roboto', system-ui, sans-serif;
  --chat--heading-font-family: 'Poppins', system-ui, sans-serif;

  /* Window */
  --chat--window--width: 420px;
  --chat--window--height: 600px;
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid var(--chat--color-light-shade-100);
  --chat--window--border-radius: var(--chat--border-radius);

  /* Header */
  --chat--header--padding: 0.75rem;                    /* 50% less height */
  --chat--header--background: var(--chat--color-primary-shade-100); /* deeper blue */
  --chat--header--color: var(--chat--color-white);
  --chat--heading--font-size: 1.8em;
  --chat--subtitle--font-size: 1rem;
  --chat--subtitle--line-height: 1.5;

  /* Messages */
  --chat--message--font-size: 1rem;
  --chat--message--padding: 0.8rem 1rem;
  --chat--message--border-radius: var(--chat--border-radius);
  --chat--message--margin-bottom: 0.35rem; /* less spacing */
  --chat--message-line-height: 1.6;
  --chat--message--bot--background: #97979738;
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--pre--background: #e8ecf1;

  /* Input */
  --chat--textarea--height: 52px;
  --chat--input--background: var(--chat--color-white);
  --chat--input--text-color: var(--chat--color-dark);
  --chat--input--padding: 0.75rem 1rem;
  --chat--input--border: 1px solid var(--chat--color-light-shade-50);
  --chat--input--border-radius: var(--chat--border-radius);

  /* Buttons */
  --chat--button--background: var(--chat--color-secondary);
  --chat--button--color: var(--chat--color-white);
  --chat--button--hover--background: var(--chat--color-secondary-shade-50);
  --chat--button--hover--color: var(--chat--color-white);
  --chat--button--padding: 0.5rem 1.2rem;
  --chat--button--border-radius: var(--chat--border-radius);

  /* Footer */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-white);
  --chat--footer--color: var(--chat--color-dark);
}

/* Structure */
body {
  margin: 0;
  font-family: var(--chat--font-family);
  background: var(--chat--body--background);
}

.chat-window {
  width: var(--chat--window--width);
  height: var(--chat--window--height);
  margin: 2rem auto;
  border: var(--chat--window--border);
  border-radius: var(--chat--window--border-radius);
  display: flex;
  flex-direction: column;
  overflow: hidden;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.1);
}

.chat-layout .chat-header {
    background-repeat: no-repeat;
    background-size: contain;
    background-position: right center;
    background-image: url(https://www.nosta.com/wp-content/themes/kd-theme/resources/img/nosta.jpg);
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
    gap: 1em;
    height: var(--chat--header-height);
    padding: var(--chat--header--padding);
    background-color: var(--chat--header--background);
    color: var(--chat--header--color);
    border-top: var(--chat--header--border-top);
    border-bottom: var(--chat--header--border-bottom);
    border-left: var(--chat--header--border-left);
    border-right: var(--chat--header--border-right);
}

.chat-logo {
  height: 40px;
  width: auto;
}

.chat-header-text h1 {
  margin: 0;
  font-family: var(--chat--heading-font-family);
  font-weight: 600;
  font-size: var(--chat--heading--font-size);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.chat-header-text p {
  margin: 0.25rem 0 0;
  font-size: var(--chat--subtitle--font-size);
  line-height: var(--chat--subtitle--line-height);
}

/* Chat body */
.chat-body {
  flex: 1;
  padding: var(--chat--spacing);
  background: var(--chat--body--background);
  overflow-y: auto;
}

.chat-message {
  padding: var(--chat--message--padding);
  margin-bottom: var(--chat--message--margin-bottom);
  border-radius: var(--chat--message--border-radius);
  font-size: var(--chat--message--font-size);
  line-height: var(--chat--message-line-height);
  max-width: 80%;
  box-shadow: 0px 5px 9px 3px rgb(0 0 0 / 12%);
  width: fit-content;
}

.chat-message.bot {
  background: var(--chat--message--bot--background);
  color: var(--chat--message--bot--color);
  border-left: 3px solid var(--chat--color-primary);
}

.chat-message.user {
  background: var(--chat--message--user--background);
  color: var(--chat--message--user--color);
  border-left: 3px solid var(--chat--color-secondary);
  margin-left: auto;
}

/* Footer */
.chat-footer {
  background: var(--chat--footer--background);
  padding: 0.5rem 1rem;
  border-top: 1px solid var(--chat--color-light-shade-100);
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.chat-footer textarea {
  flex-grow: 1;
  resize: none;
  padding: var(--chat--input--padding);
  height: var(--chat--textarea--height);
  border: var(--chat--input--border);
  border-radius: var(--chat--input--border-radius);
  font-family: var(--chat--font-family);
  font-size: 1rem;
  color: var(--chat--input--text-color);
  background: var(--chat--input--background);
}

.chat-send-button {
  background: var(--chat--button--background);
  color: var(--chat--button--color);
  border: none;
  padding: var(--chat--button--padding);
  border-radius: var(--chat--button--border-radius);
  font-size: 1.25rem;
  cursor: pointer;
  transition: background 0.2s;
}

.chat-send-button:hover {
  background: var(--chat--button--hover--background);
}

img {
    width: -webkit-fill-available;
}
